import React, {Component} from 'react';
import './App.css';
import {FormInput} from "./components/FormInput";
import './css/Main.css'
import {MainSection} from "./components/MainSection";
import {Footer} from "./components/Footer";

class App extends Component {

    constructor(props) {
        super(props);
        this.state = {
            todos: [],
            isCheckAll: false
        }
    }

    addToDo = input_txt => {
        let {todos} = this.state;

        let newTodo = {
            id: this.state.todos.reduce((maxId, e) => Math.max(maxId, e.id), -1) + 1,
            name: input_txt,
            isComplete: false
        }

        let newTodos = [...todos, newTodo];
        this.setState({todos: newTodos});
    }


    deleteTodo = todo_id => {
        let todos = this.state.todos.filter(element => element.id !== todo_id);
        this.setState({todos: todos});
    }

    completeTodo = (todo_id, isCheck) => {
        let todos = this.state.todos.map(element => {
            if (element.id === todo_id) {
                element.isComplete = isCheck;
            }
            return element;
        });
        this.setState({todos});
    }

    completeAllTodo = (isCheckAll) => {
        let todos = this.state.todos.map(element => {
            element.isComplete = isCheckAll ? true : false;
            return element;
        });
        this.setState({todos, isCheckAll});
    }

    clearComplete = () => {
        let todos = this.state.todos.filter(element => !element.isComplete);
        this.setState({todos});
    }


    render() {
        return (
            <div className="container">
                <FormInput addToDo={this.addToDo} completeAllTodo={this.completeAllTodo} isCheckAll={this.state.isCheckAll}/>
                <MainSection mainSectionProps={this.state.todos}
                             deleteTodo={this.deleteTodo}
                             completeTodo={this.completeTodo}/>
                <Footer clearComplete={this.clearComplete}/>
            </div>
        );
    }
}

export default App;
